import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  Search,
  Eye,
  RefreshCw,
  BarChart3,
  Clock,
  CheckCircle,
  AlertCircle,
  Globe,
  Download,
  Activity,
  Zap,
  Users,
  X,
  FileText,
} from 'lucide-react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const ModernScanManagement = () => {
  const [scans, setScans] = useState([]);
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterClient, setFilterClient] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [showTriggerModal, setShowTriggerModal] = useState(false);
  const [showResultsModal, setShowResultsModal] = useState(false);
  const [scanResults, setScanResults] = useState(null);
  const [triggerData, setTriggerData] = useState({
    clientId: '',
    keywords: [],
    region: 'US',
  });
  const [triggering, setTriggering] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);

  useEffect(() => {
    fetchScans();
    fetchClients();
  }, []);

  const fetchScans = async () => {
    try {
      const response = await api.get('/scans');
      setScans(response.data);
    } catch (error) {
      toast.error('Failed to load scans');
      console.error('Scans error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchClients = async () => {
    try {
      const response = await api.get('/clients');
      setClients(response.data);
    } catch (error) {
      toast.error('Failed to load clients');
      console.error('Clients error:', error);
    }
  };

  const triggerScan = async () => {
    if (!triggerData.clientId) {
      toast.error('Please select a client');
      return;
    }

    if (!triggerData.keywords || triggerData.keywords.length === 0) {
      toast.error('Please add at least one keyword');
      return;
    }

    setTriggering(true);
    setCurrentStep(1);

    try {
      // Step 1: Get ALL URLs from Google Search for each keyword
      toast.loading('Step 1: Fetching ALL search results...', { id: 'scan-progress' });

      let allSearchResults = [];

      // Search for each keyword individually to get comprehensive results
      for (const keyword of triggerData.keywords) {
        toast.loading(`Searching for: ${keyword}...`, { id: 'scan-progress' });

        // Combine client name with keyword for better search results
        const selectedClient = clients.find(c => c._id === triggerData.clientId);
        const clientName = selectedClient?.name || '';
        const enhancedKeyword = clientName ? `${clientName} ${keyword}` : keyword;

        const searchResponse = await api.post('/orm-scan/test/google-search', {
          keywords: [enhancedKeyword],
          region: triggerData.region,
          numResults: 50 // Get many more results per keyword
        });

        if (searchResponse.data.success && searchResponse.data.results) {
          allSearchResults.push(...searchResponse.data.results);
        }

        // Small delay between searches to avoid rate limits
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      // Remove duplicates based on URL
      const uniqueResults = allSearchResults.filter((result, index, self) => 
        index === self.findIndex(r => r.link === result.link)
      );

      console.log(`ðŸ“Š Found ${uniqueResults.length} unique search results`);

      if (uniqueResults.length === 0) {
        throw new Error('No search results found for the keywords');
      }

      setCurrentStep(2);

      // Step 2: Get client data for OpenAI analysis
      const selectedClient = clients.find(c => c._id === triggerData.clientId);
      const clientData = {
        name: selectedClient?.name,
        industry: selectedClient?.industry,
        company: selectedClient?.company,
        website: selectedClient?.website,
        targetAudience: selectedClient?.targetAudience,
        keywords: selectedClient?.keywords,
        reputationGoals: selectedClient?.reputationGoals,
        currentChallenges: selectedClient?.currentChallenges
      };

      // Step 3: Analyze sentiment with OpenAI for ALL results
      toast.loading(`Step 2: Analyzing ${uniqueResults.length} links with OpenAI...`, { id: 'scan-progress' });
      const sentimentResponse = await api.post('/orm-scan/test/sentiment-analysis', {
        links: uniqueResults,
        clientData: clientData
      });

      if (!sentimentResponse.data.success) {
        throw new Error('Failed to analyze sentiment with OpenAI');
      }

      setCurrentStep(3);

      // Step 4: Store scan and results
      toast.loading('Step 3: Storing scan results...', { id: 'scan-progress' });

      // Create scan record
      const scanResponse = await api.post('/scans', {
        clientId: triggerData.clientId,
        clientName: selectedClient?.name,
        keywords: triggerData.keywords,
        region: triggerData.region,
        scanType: 'manual',
        resultsCount: sentimentResponse.data.results.length
      });

      if (!scanResponse.data.success) {
        throw new Error('Failed to store scan');
      }

      // Store scan results
      const resultsResponse = await api.post(`/scans/${scanResponse.data.scan._id}/results`, {
        results: sentimentResponse.data.results
      });

      if (!resultsResponse.data.success) {
        throw new Error('Failed to store scan results');
      }

      const analyzedResults = sentimentResponse.data.results;

      const results = {
        clientId: triggerData.clientId,
        clientName: selectedClient?.name,
        keywords: triggerData.keywords,
        region: triggerData.region,
        totalResults: analyzedResults.length,
        uniqueResults: uniqueResults.length,
        sentimentBreakdown: {
          positive: analyzedResults.filter(r => r.sentiment === 'positive').length,
          negative: analyzedResults.filter(r => r.sentiment === 'negative').length,
          neutral: analyzedResults.filter(r => r.sentiment === 'neutral').length
        },
        results: analyzedResults,
        scanDate: new Date().toISOString(),
        summary: {
          totalLinksFound: uniqueResults.length,
          linksAnalyzed: analyzedResults.length,
          positiveLinks: analyzedResults.filter(r => r.sentiment === 'positive').length,
          negativeLinks: analyzedResults.filter(r => r.sentiment === 'negative').length,
          neutralLinks: analyzedResults.filter(r => r.sentiment === 'neutral').length,
          averageConfidence: analyzedResults.reduce((sum, r) => sum + r.confidence, 0) / analyzedResults.length
        }
      };

      setScanResults(results);
      setShowTriggerModal(false);
      setShowResultsModal(true);

      toast.success('Scan completed successfully!', { id: 'scan-progress' });
      await fetchScans();
    } catch (error) {
      console.error('Scan error:', error);
      toast.error(`Scan failed: ${error.message}`, { id: 'scan-progress' });
    } finally {
