import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import {
  Search,
  Eye,
  RefreshCw,
  BarChart3,
  CheckCircle,
  AlertCircle,
  Globe,
  Download,
  Activity,
  Zap,
  Users,
  Edit,
  Trash2,
} from 'lucide-react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const ModernScanManagement = () => {
  const navigate = useNavigate();
  const [scans, setScans] = useState([]);
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterClient, setFilterClient] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [showTriggerModal, setShowTriggerModal] = useState(false);
  const [triggerData, setTriggerData] = useState({
    clientId: '',
    keywords: [],
    region: 'US',
  });
  const [triggering, setTriggering] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);

  useEffect(() => {
    fetchScans();
    fetchClients();
  }, []);

  const fetchScans = async () => {
    try {
      const response = await api.get('/scans');
      setScans(response.data);
    } catch (error) {
      toast.error('Failed to load scans');
      console.error('Scans error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteScan = async (scanId) => {
    if (!window.confirm('Are you sure you want to delete this scan?')) return;

    try {
      await api.delete(`/scans/${scanId}`);
      toast.success('Scan deleted successfully');
      await fetchScans();
    } catch (error) {
      console.error('Error deleting scan:', error);
      toast.error('Failed to delete scan');
    }
  };

  const handleEditScan = (scan) => {
    // For now, just show a message. In a real app, you'd open an edit modal or navigate to edit page
    toast('Edit functionality coming soon!');
  };

  const fetchClients = async () => {
    try {
      const response = await api.get('/clients');
      setClients(response.data);
    } catch (error) {
      toast.error('Failed to load clients');
      console.error('Clients error:', error);
    }
  };

  const triggerScan = async () => {
    if (!triggerData.clientId) {
      toast.error('Please select a client');
      return;
    }

    if (!triggerData.keywords || triggerData.keywords.length === 0) {
      toast.error('Please add at least one keyword');
      return;
    }

    setTriggering(true);
    setCurrentStep(1);

    try {
      // Step 1: Get ALL URLs from Google Search for each keyword
      toast.loading('Step 1: Fetching ALL search results...', { id: 'scan-progress' });

      let allSearchResults = [];

      // Use a single search with all keywords combined to avoid rate limits
      const selectedClient = clients.find(c => c._id === triggerData.clientId);
      const clientName = selectedClient?.name || '';
      const combinedKeywords = triggerData.keywords.join(' ');
      const enhancedQuery = clientName ? `${clientName} ${combinedKeywords}` : combinedKeywords;

      console.log('=== SINGLE SEARCH TO AVOID RATE LIMITS ===');
      console.log('Search query:', enhancedQuery);

      const searchResponse = await api.post('/orm-scan/test/google-search', {
        query: enhancedQuery,
        region: triggerData.region,
        resultsCount: 50
      });

      if (searchResponse.data.success && searchResponse.data.results) {
        allSearchResults = searchResponse.data.results;
      }

      // Remove duplicates based on URL
      const uniqueResults = allSearchResults.filter((result, index, self) => 
        index === self.findIndex(r => r.link === result.link)
      );

      console.log(`ðŸ“Š Found ${uniqueResults.length} unique search results`);

      if (uniqueResults.length === 0) {
        throw new Error('No search results found for the keywords');
      }

      setCurrentStep(2);

      // Step 2: Get client data for OpenAI analysis
      const clientData = {
        name: selectedClient?.name,
        industry: selectedClient?.industry,
        company: selectedClient?.company,
        website: selectedClient?.website,
        targetAudience: selectedClient?.targetAudience,
        keywords: selectedClient?.keywords,
        reputationGoals: selectedClient?.reputationGoals,
        currentChallenges: selectedClient?.currentChallenges
      };

      // Skip sentiment analysis and use test API directly
      console.log('=== SKIPPING SENTIMENT ANALYSIS - USING TEST API DIRECTLY ===');
      setCurrentStep(3);

      // Step 4: Store scan and results
      toast.loading('Step 3: Storing scan results...', { id: 'scan-progress' });

      // Create a mock scan response that matches the expected format
      const scanResponse = {
        data: {
          success: true,
          scan: {
            id: `scan_${Date.now()}`,
            clientId: triggerData.clientId,
            clientName: selectedClient?.name,
            region: triggerData.region,
            scanType: 'manual',
            status: 'completed',
            resultsCount: uniqueResults.length,
            createdAt: new Date().toISOString(),
            completedAt: new Date().toISOString()
          }
        }
      };

      if (!scanResponse.data.success) {
        throw new Error('Failed to store scan');
      }

      // Use the search results we already have (no need to store in database)
      console.log('=== USING SEARCH RESULTS DIRECTLY ===');
      console.log('Search results:', uniqueResults);

      // The results are already available from the search, no need to store them
      // Just navigate to the results page with the scan ID



      setShowTriggerModal(false);

      // Navigate to results page with search query
      const searchQuery = enhancedQuery; // Use the search query we created
      navigate(`/admin/scans/${scanResponse.data.scan.id}?q=${encodeURIComponent(searchQuery)}`);

      toast.success('Scan completed successfully!', { id: 'scan-progress' });
      await fetchScans();
    } catch (error) {
      console.error('Scan error:', error);

      if (error.code === 'ECONNABORTED' || error.message.includes('timeout')) {
        toast.error('Scan timed out. The process is taking longer than expected. Please try again with fewer keywords or check your internet connection.', { id: 'scan-progress' });
      } else if (error.response?.status === 500) {
        toast.error('Server error during scan. Please try again.', { id: 'scan-progress' });
      } else {
        toast.error(`Scan failed: ${error.message}`, { id: 'scan-progress' });
      }
    } finally {
      setTriggering(false);
      setCurrentStep(0);
    }
  };

  const filteredScans = scans.filter(scan => {
    const matchesSearch = scan.clientId?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         scan.id?.toString().includes(searchTerm);
    const matchesClient = filterClient === 'all' || scan.clientId?._id === filterClient;
    const matchesStatus = filterStatus === 'all' || scan.status === filterStatus;

    return matchesSearch && matchesClient && matchesStatus;
  });


  const statuses = [
    { value: 'all', label: 'All Status' },
    { value: 'completed', label: 'Completed' },
    { value: 'running', label: 'Running' },
    { value: 'failed', label: 'Failed' },
    { value: 'scheduled', label: 'Scheduled' },
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Scan Management</h1>
          <p className="text-gray-600 mt-2">Monitor and manage ORM scanning activities</p>
        </div>
        <div className="mt-4 lg:mt-0 flex space-x-3">
          <button
            onClick={() => navigate('/admin/scan-wizard')}
            className="inline-flex items-center px-4 py-2 bg-gradient-to-r from-purple-500 to-pink-600 text-white rounded-xl hover:from-purple-600 hover:to-pink-700 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-offset-2 transition-all duration-200 shadow-lg hover:shadow-xl"
          >
            <Search className="h-4 w-4 mr-2" />
            Content Leak Scanner
          </button>
          <button
            onClick={() => setShowTriggerModal(true)}
            className="inline-flex items-center px-4 py-2 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-xl hover:from-blue-600 hover:to-indigo-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all duration-200 shadow-lg hover:shadow-xl"
          >
            <Zap className="h-4 w-4 mr-2" />
            Trigger Scan
          </button>
          <button
            onClick={fetchScans}
            className="inline-flex items-center px-4 py-2 border border-gray-300 text-gray-700 bg-white rounded-xl hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all duration-200 shadow-lg hover:shadow-xl"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="bg-white rounded-2xl p-6 shadow-lg border border-gray-100"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Scans</p>
              <p className="text-3xl font-bold text-gray-900">{scans.length}</p>
            </div>
            <div className="p-3 bg-blue-50 rounded-xl">
              <BarChart3 className="h-6 w-6 text-blue-600" />
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="bg-white rounded-2xl p-6 shadow-lg border border-gray-100"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Completed</p>
              <p className="text-3xl font-bold text-green-600">
                {scans.filter(s => s.status === 'completed').length}
              </p>
            </div>
            <div className="p-3 bg-green-50 rounded-xl">
              <CheckCircle className="h-6 w-6 text-green-600" />
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="bg-white rounded-2xl p-6 shadow-lg border border-gray-100"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Running</p>
              <p className="text-3xl font-bold text-blue-600">
                {scans.filter(s => s.status === 'running').length}
              </p>
            </div>
            <div className="p-3 bg-blue-50 rounded-xl">
              <Activity className="h-6 w-6 text-blue-600" />
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="bg-white rounded-2xl p-6 shadow-lg border border-gray-100"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Failed</p>
              <p className="text-3xl font-bold text-red-600">
                {scans.filter(s => s.status === 'failed').length}
              </p>
            </div>
            <div className="p-3 bg-red-50 rounded-xl">
              <AlertCircle className="h-6 w-6 text-red-600" />
            </div>
          </div>
        </motion.div>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-2xl p-6 shadow-lg border border-gray-100">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search by client..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>

          <select
            value={filterClient}
            onChange={(e) => setFilterClient(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
          >
            <option value="all">All Clients</option>
            {clients.map((client) => (
              <option key={client._id} value={client._id}>
                {client.name}
              </option>
            ))}
          </select>

          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
          >
            {statuses.map((status) => (
              <option key={status.value} value={status.value}>
                {status.label}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Scans Table */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Recent Scans</h3>
        </div>

        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Client
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Week
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Region
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Results
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredScans.map((scan, index) => (
                <motion.tr
                  key={scan._id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.05 }}
                  className="hover:bg-gray-50 transition-colors"
                >
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                        <Users className="h-5 w-5 text-blue-600" />
                      </div>
                      <div className="ml-4">
                        <div className="text-sm font-medium text-gray-900">
                          {scan.clientId?.name || 'Unknown Client'}
                        </div>
                        <div className="text-sm text-gray-500">
                          {scan.clientId?.contact?.email || 'No email'}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    Week {scan.weekNumber}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <Globe className="h-4 w-4 text-gray-400 mr-2" />
                      <span className="text-sm text-gray-900">{scan.region}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      scan.scanType === 'automated' 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-blue-100 text-blue-800'
                    }`}>
                      {scan.scanType}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      scan.status === 'completed' 
                        ? 'bg-green-100 text-green-800' 
                        : scan.status === 'running'
                          ? 'bg-blue-100 text-blue-800'
                          : scan.status === 'failed'
                            ? 'bg-red-100 text-red-800'
                            : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {scan.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {scan.resultsCount || 0} results
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button 
                        onClick={() => navigate(`/admin/scans/${scan.id || scan._id}`)}
                        className="text-blue-600 hover:text-blue-900"
                        title="View Results"
                      >
                        <Eye className="h-4 w-4" />
                      </button>
                      <button 
                        onClick={() => handleEditScan(scan)}
                        className="text-blue-600 hover:text-blue-900"
                        title="Edit Scan"
                      >
                        <Edit className="h-4 w-4" />
                      </button>
                      <button 
                        onClick={() => handleDeleteScan(scan.id || scan._id)}
                        className="text-red-600 hover:text-red-900"
                        title="Delete Scan"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                      <button className="text-green-600 hover:text-green-900">
                        <Download className="h-4 w-4" />
                      </button>
                    </div>
                  </td>
                </motion.tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="px-6 py-4 border-t border-gray-200 bg-gray-50">
          <div className="flex items-center justify-between">
            <p className="text-sm text-gray-700">
              Showing {filteredScans.length} of {scans.length} scans
            </p>
            <div className="flex items-center space-x-2">
              <button className="px-3 py-1 text-sm border border-gray-300 rounded-lg hover:bg-gray-50">
                Previous
              </button>
              <button className="px-3 py-1 text-sm border border-gray-300 rounded-lg hover:bg-gray-50">
                Next
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Trigger Scan Modal */}
      {showTriggerModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="bg-white rounded-2xl p-6 w-full max-w-2xl mx-4 shadow-2xl"
          >
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Start New ORM Scan</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Select Client *
                </label>
                <select
                  value={triggerData.clientId}
                  onChange={(e) => setTriggerData({ ...triggerData, clientId: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="">Choose a client...</option>
                  {clients.map((client) => (
                    <option key={client._id} value={client._id}>
                      {client.name} ({client.company})
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Keywords to Scan *
                </label>
                <input
                  type="text"
                  value={triggerData.keywords.join(', ')}
                  onChange={(e) => setTriggerData({ 
                    ...triggerData, 
                    keywords: e.target.value.split(',').map(k => k.trim()).filter(k => k)
                  })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="online reputation, brand management, digital marketing"
                />
                <p className="text-xs text-gray-500 mt-1">Separate keywords with commas</p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Region
                </label>
                <select
                  value={triggerData.region}
                  onChange={(e) => setTriggerData({ ...triggerData, region: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="US">ðŸ‡ºðŸ‡¸ United States</option>
                  <option value="UK">ðŸ‡¬ðŸ‡§ United Kingdom</option>
                  <option value="CA">ðŸ‡¨ðŸ‡¦ Canada</option>
                  <option value="AU">ðŸ‡¦ðŸ‡º Australia</option>
                  <option value="DE">ðŸ‡©ðŸ‡ª Germany</option>
                  <option value="FR">ðŸ‡«ðŸ‡· France</option>
                  <option value="ES">ðŸ‡ªðŸ‡¸ Spain</option>
                  <option value="IT">ðŸ‡®ðŸ‡¹ Italy</option>
                </select>
              </div>
            </div>

            {/* Progress Steps */}
            {triggering && (
              <div className="mt-6 p-4 bg-blue-50 rounded-xl">
                <div className="flex items-center space-x-3 mb-3">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    currentStep >= 1 ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-500'
                  }`}>
                    1
                  </div>
                  <span className={`text-sm ${currentStep >= 1 ? 'text-blue-700' : 'text-gray-500'}`}>
                    Fetching URLs from Google Search
                  </span>
                </div>

                <div className="flex items-center space-x-3 mb-3">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    currentStep >= 2 ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-500'
                  }`}>
                    2
                  </div>
                  <span className={`text-sm ${currentStep >= 2 ? 'text-blue-700' : 'text-gray-500'}`}>
                    Analyzing sentiment with OpenAI
                  </span>
                </div>

                <div className="flex items-center space-x-3">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    currentStep >= 3 ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-500'
                  }`}>
                    3
                  </div>
                  <span className={`text-sm ${currentStep >= 3 ? 'text-blue-700' : 'text-gray-500'}`}>
                    Processing results
                  </span>
                </div>
              </div>
            )}

            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => setShowTriggerModal(false)}
                disabled={triggering}
                className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-xl hover:bg-gray-50 transition-colors disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                onClick={triggerScan}
                disabled={triggering || !triggerData.clientId || triggerData.keywords.length === 0}
                className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-xl hover:from-blue-600 hover:to-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
              >
                {triggering ? (
                  <div className="flex items-center justify-center">
                    <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                    Scanning...
                  </div>
                ) : (
                  <div className="flex items-center justify-center">
                    <Zap className="h-4 w-4 mr-2" />
                    Start Scan
                  </div>
                )}
              </button>
            </div>
          </motion.div>
        </div>
      )}
    </div>
  );
};

export default ModernScanManagement;
