import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import { motion } from 'framer-motion';
import {
  Search,
  Filter,
  Download,
  Eye,
  FileText,
  Calendar,
  BarChart3,
  Globe,
  TrendingUp,
  TrendingDown,
  Plus,
  Minus,
} from 'lucide-react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const ClientReports = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRegion, setFilterRegion] = useState('all');
  const [filterWeek, setFilterWeek] = useState('all');

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await api.get('/reports/my-reports');
      setReports(response.data);
    } catch (error) {
      toast.error('Failed to load reports');
      console.error('Reports error:', error);
    } finally {
      setLoading(false);
    }
  };

  const downloadPDF = async (reportId) => {
    try {
      const response = await api.get(`/reports/${reportId}/my-download/pdf`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report_${reportId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
      toast.success('PDF downloaded successfully');
    } catch (error) {
      toast.error('Failed to download PDF');
    }
  };

  const downloadExcel = async (reportId) => {
    try {
      const response = await api.get(`/reports/${reportId}/my-download/excel`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report_${reportId}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
      toast.success('Excel file downloaded successfully');
    } catch (error) {
      toast.error('Failed to download Excel file');
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      generating: { color: 'text-blue-800', bg: 'bg-blue-100' },
      completed: { color: 'text-green-800', bg: 'bg-green-100' },
      failed: { color: 'text-red-800', bg: 'bg-red-100' },
    };

    const config = statusConfig[status] || statusConfig.generating;

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${config.bg} ${config.color}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  const filteredReports = reports.filter((report) => {
    const matchesSearch = report.clientId?.name?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesRegion = filterRegion === 'all' || report.region === filterRegion;
    const matchesWeek = filterWeek === 'all' || report.weekNumber.toString() === filterWeek;
    return matchesSearch && matchesRegion && matchesWeek;
  });

  const regions = [
    { code: 'US', name: 'United States', flag: 'ðŸ‡ºðŸ‡¸' },
    { code: 'UK', name: 'United Kingdom', flag: 'ðŸ‡¬ðŸ‡§' },
    { code: 'UAE', name: 'UAE', flag: 'ðŸ‡¦ðŸ‡ª' },
    { code: 'CA', name: 'Canada', flag: 'ðŸ‡¨ðŸ‡¦' },
    { code: 'AU', name: 'Australia', flag: 'ðŸ‡¦ðŸ‡º' },
  ];

  if (loading) {
    return (
      <Layout>
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-ace-600"></div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">My Reports</h1>
            <p className="text-gray-600">View and download your ORM reports</p>
          </div>
          <button className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-ace-600 hover:bg-ace-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-ace-500">
            <Download className="h-4 w-4 mr-2" />
            Download All
          </button>
        </div>

        {/* Filters */}
        <div className="bg-white shadow-ace rounded-lg p-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Search Reports</label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search reports..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-ace-500 focus:border-ace-500 sm:text-sm"
                />
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Region</label>
              <select
                value={filterRegion}
                onChange={(e) => setFilterRegion(e.target.value)}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-ace-500 focus:border-ace-500 sm:text-sm"
              >
                <option value="all">All Regions</option>
                {regions.map((region) => (
                  <option key={region.code} value={region.code}>
                    {region.flag} {region.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Filter by Week</label>
              <select
                value={filterWeek}
                onChange={(e) => setFilterWeek(e.target.value)}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-ace-500 focus:border-ace-500 sm:text-sm"
              >
                <option value="all">All Weeks</option>
                {Array.from({ length: 12 }, (_, i) => i + 1).map((week) => (
                  <option key={week} value={week.toString()}>
                    Week {week}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Reports Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredReports.map((report) => (
            <motion.div
              key={report._id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white shadow-ace rounded-lg overflow-hidden hover:shadow-lg transition-shadow"
            >
              <div className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center">
                    <div className="h-10 w-10 bg-ace-100 rounded-lg flex items-center justify-center">
                      <BarChart3 className="h-5 w-5 text-ace-600" />
                    </div>
                    <div className="ml-3">
                      <h3 className="text-lg font-medium text-gray-900">
                        Week {report.weekNumber}
                      </h3>
                      <p className="text-sm text-gray-500">{report.region}</p>
                    </div>
                  </div>
                  {getStatusBadge(report.status)}
                </div>

                <div className="space-y-3 mb-4">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Total Links</span>
                    <span className="text-sm font-medium text-gray-900">
                      {report.summary?.totalLinks || 0}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Positive</span>
                    <span className="text-sm font-medium text-green-600">
                      {report.summary?.positiveLinks || 0}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Negative</span>
                    <span className="text-sm font-medium text-red-600">
                      {report.summary?.negativeLinks || 0}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">New Links</span>
                    <span className="text-sm font-medium text-blue-600">
                      {report.summary?.newLinks || 0}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Suppressed</span>
                    <span className="text-sm font-medium text-purple-600">
                      {report.summary?.suppressedLinks || 0}
                    </span>
                  </div>
                </div>

                <div className="text-xs text-gray-500 mb-4">
                  Generated: {new Date(report.generatedAt).toLocaleDateString()}
                </div>

                <div className="flex space-x-2">
                  <button
                    onClick={() => downloadPDF(report._id)}
                    className="flex-1 inline-flex items-center justify-center px-3 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                  >
                    <FileText className="h-4 w-4 mr-1" />
                    PDF
                  </button>
                  <button
                    onClick={() => downloadExcel(report._id)}
                    className="flex-1 inline-flex items-center justify-center px-3 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                  >
                    <Download className="h-4 w-4 mr-1" />
                    Excel
                  </button>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {filteredReports.length === 0 && (
          <div className="text-center py-12">
            <BarChart3 className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No reports found</h3>
            <p className="mt-1 text-sm text-gray-500">
              {searchTerm || filterRegion !== 'all' || filterWeek !== 'all'
                ? 'Try adjusting your filters to see more results.'
                : 'Reports will appear here once scans are completed.'}
            </p>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default ClientReports;
